    }

    # Define the schema
    schema = {
        'Interface': lambda x, y: x in entities['Software System'] and y in entities['Environment Object'],
        'Requirements Reference': lambda x, y: x in entities['System Requirements'] and y in entities,
        'Requirements Constraint': lambda x, y: x in entities['System Requirements'] and y in entities['Shared Phenomena']
    }

    # Tokenize the sentence
    tokens = sentence.split()

    # Extract entities
    for entity in entities:
        for token in tokens:
            if token in entities[entity]:
                relation['Phenomena Interface'].append([token, entity])

    # Extract relations
    for relation_type in schema:
        for entity in entities:
            for token in tokens:
                if token in entities[entity]:
                    for other_entity in entities:
                        for other_token in tokens:
                            if other_token in entities[other_entity] and schema[relation_type](token, other_token):
                                if relation_type == 'Interface':
                                    relation['Phenomena Interface'].append([token, other_token])
                                elif relation_type == 'Requirements Reference':
                                    relation['requirements reference'].append([token, other_token])
                                elif relation_type == 'Requirements Constraint':
                                    relation['requirements constraint'].append([token, other_token])

    return json.dumps(relation)

