    schema_definition = {
        'Interface': 'Phenomena Interface',
        'Requirements Reference': 'requirements reference',
        'Requirements Constraint': 'requirements constraint'
    }

    # Initialize the result dictionary
    result = {key: [] for key in schema_definition.values()}

    # Extract entities
    entity_list = []
    for key, value in entities.items():
        if value:
            entity_list.extend(value)

    # Extract relations
    for entity in entity_list:
        for key, value in entities.items():
            if value and entity in value:
                for other_entity in entity_list:
                    if other_entity != entity:
                        if other_entity in entities['System Requirements']:
                            if re.search(r'\b' + entity + r'\b', entities['System Requirements'][entities['System Requirements'].index(other_entity)]):
                                if schema_definition['Requirements Constraint'] not in result:
                                    result[schema_definition['Requirements Constraint']] = []
                                result[schema_definition['Requirements Constraint']].append([entity, other_entity])
                        elif other_entity in entities['Shared Phenomena']:
                            if re.search(r'\b' + entity + r'\b', entities['Shared Phenomena'][entities['Shared Phenomena'].index(other_entity)]):
                                if schema_definition['Requirements Reference'] not in result:
                                    result[schema_definition['Requirements Reference']] = []
                                result[schema_definition['Requirements Reference']].append([entity, other_entity])
                        elif other_entity in entities['Software System']:
                            if re.search(r'\b' + entity + r'\b', entities['Software System'][entities['Software System'].index(other_entity)]):
                                if schema_definition['Interface'] not in result:
                                    result[schema_definition['Interface']] = []
                                result[schema_definition['Interface']].append([entity, other_entity])

    # Return the result as a JSON string
    return json.dumps(result)

