import json
import re

def extract_relation(sentence, entities):
    # Define the schema definition
    schema_definition = {
        'Interface': 'is an interface of sending/receving data between the connected entities.',
        'Requirements Reference': 'is reference relation between requirements domain and other entities.',
        'Requirements Constraint': 'is constrain relation between requirements domain and other entities. It means the requirements domains does not just refer to the phenomena but constrains them.'
    }

    # Define the relation extraction rules
    relation_extraction_rules = {
        'Interface': lambda x, y: re.search(r'(\w+) will be able to (\w+) (\w+)', x) and (y.get('Environment Object', []) or y.get('External System', [])),
        'Requirements Reference': lambda x, y: re.search(r'(\w+) shall be able to (\w+) (\w+)', x) and (y.get('System Requirements', []) or y.get('Shared Phenomena', [])),
        'Requirements Constraint': lambda x, y: re.search(r'(\w+) shall be able to (\w+) (\w+)', x) and (y.get('System Requirements', []) or y.get('Shared Phenomena', []))
    }

    # Initialize the result
    result = {'Phenomena Interface': [], 'requirements reference': [], 'requirements constraint': []}

    # Extract relations
    for relation_type, rule in relation_extraction_rules.items():
        if rule(sentence, entities):
            if relation_type == 'Interface':
                entities_list = entities.get('Environment Object', []) + entities.get('External System', [])
                for entity in entities_list:
                    if re.search(r'\b' + entity + r'\b', sentence):
                        result['Phenomena Interface'].append([re.search(r'\b' + sentence.split('will be able to')[0] + r'\b', sentence).group(), entity])
            elif relation_type == 'Requirements Reference' or relation_type == 'Requirements Constraint':
                entities_list = entities.get('System Requirements', []) + entities.get('Shared Phenomena', [])
                for entity in entities_list:
                    if re.search(r'\b' + entity + r'\b', sentence):
                        result[relation_type].append([re.search(r'\b' + sentence.split('shall be able to')[0] + r'\b', sentence).group(), entity])

    # Return the result as a JSON string
    return json.dumps(result)

