    requirements_reference = []
    requirements_constraint = []

    # Extract entities
    software_system = entities.get('Software System', [])
    physical_device = entities.get('Physical Device', [])
    environment_object = entities.get('Environment Object', [])
    external_system = entities.get('External System', [])
    system_requirements = entities.get('System Requirements', [])
    shared_phenomena = entities.get('Shared Phenomena', [])

    # Extract relations
    for requirement in system_requirements:
        if 'to' in requirement:
            requirement_parts = requirement.split('to')
            if len(requirement_parts) == 2:
                requirement, target = requirement_parts
                if target in software_system or target in physical_device or target in environment_object or target in external_system:
                    if target in software_system:
                        phenomena_interface.append([requirement, target])
                    elif target in physical_device:
                        phenomena_interface.append([requirement, target])
                    elif target in environment_object:
                        phenomena_interface.append([requirement, target])
                    elif target in external_system:
                        phenomena_interface.append([requirement, target])
                    requirements_reference.append([requirement, target])
                elif target in shared_phenomena:
                    if target in software_system:
                        phenomena_interface.append([requirement, target])
                    elif target in physical_device:
                        phenomena_interface.append([requirement, target])
                    elif target in environment_object:
                        phenomena_interface.append([requirement, target])
                    elif target in external_system:
                        phenomena_interface.append([requirement, target])
                    requirements_constraint.append([requirement, target])
        else:
            if requirement in software_system or requirement in physical_device or requirement in environment_object or requirement in external_system:
                if requirement in software_system:
                    phenomena_interface.append([requirement, requirement])
                elif requirement in physical_device:
                    phenomena_interface.append([requirement, requirement])
                elif requirement in environment_object:
                    phenomena_interface.append([requirement, requirement])
                elif requirement in external_system:
                    phenomena_interface.append([requirement, requirement])
                requirements_reference.append([requirement, requirement])
            elif requirement in shared_phenomena:
                if requirement in software_system:
                    phenomena_interface.append([requirement, requirement])
                elif requirement in physical_device:
                    phenomena_interface.append([requirement, requirement])
                elif requirement in environment_object:
                    phenomena_interface.append([requirement, requirement])
                elif requirement in external_system:
                    phenomena_interface.append([requirement, requirement])
                requirements_constraint.append([requirement, requirement])

    # Extract relations between entities
    for entity in software_system:
        for other_entity in physical_device + environment_object + external_system:
            phenomena_interface.append([entity, other_entity])
    for entity in physical_device:
        for other_entity in environment_object + external_system:
            phenomena_interface.append([entity, other_entity])
    for entity in environment_object:
        for other_entity in external_system:
            phenomena_interface.append([entity, other_entity])

    # Return relations
    return {
        'Phenomena Interface': phenomena_interface,
        'requirements reference': requirements_reference,
        'requirements constraint': requirements_constraint
    }

