                    result[schema_definition['Requirements Constraint']].append([subject, object])
                else:
                    result[schema_definition['Requirements Reference']].append([subject, object])
            elif entity_type == 'Shared Phenomena':
                if subject in entities['Software System'] or subject in entities['Physical Device']:
                    result[schema_definition['Requirements Constraint']].append([subject, object])
                else:
                    result[schema_definition['Requirements Reference']].append([subject, object])
            elif entity_type == 'Software System' or entity_type == 'Physical Device':
                if object in entities['System Requirements'] or object in entities['Shared Phenomena']:
                    result[schema_definition['Requirements Constraint']].append([subject, object])
