                    for j in range(i + 1, len(tokens)):
                        if tokens[j] in entity_dict and entity_dict[tokens[j]] in ['Software System', 'Physical Device', 'Environment Object', 'External System']:
                            # Check if the token is a relation
                            if tokens[j] in schema_definition:
                                # Add the relation to the relations dictionary
                                relations[schema_definition[tokens[j]]].append([tokens[i], tokens[j]])
                # Check if the token is an object
                else:
                    # Check if the token is an object of a relation
