    }

    # Extract entities
    entity_list = []
    for key, value in entities.items():
        entity_list.extend(value)

    # Extract relation
    for entity in entity_list:
        if entity in sentence:
            for other_entity in entity_list:
                if other_entity != entity:
                    if other_entity in sentence:
                        if 'shall' in sentence:
                            if entity in sentence.split('shall')[1].split(' ')[0]:
                                relation['Phenomena Interface'].append([entity, other_entity])
                            elif other_entity in sentence.split('shall')[1].split(' ')[0]:
                                relation['Phenomena Interface'].append([other_entity, entity])
                        elif 'shall' not in sentence:
                            relation['Phenomena Interface'].append([entity, other_entity])

    # Extract requirements reference
    for entity in entity_list:
        if entity in sentence:
            for requirement in entities['System Requirements']:
                if requirement in sentence:
                    relation['requirements reference'].append([entity, requirement])

    # Extract requirements constraint
    for entity in entity_list:
        if entity in sentence:
            for requirement in entities['System Requirements']:
                if requirement in sentence:
                    if 'shall' in sentence:
                        if entity in sentence.split('shall')[1].split(' ')[0]:
                            relation['requirements constraint'].append([entity, requirement])
                    elif 'shall' not in sentence:
                        relation['requirements constraint'].append([entity, requirement])

    return json.dumps(relation)

