import re
import json

def extract_relation(sentence, entities):
    # Define the schema definition
    schema_definition = {
        'Interface': 'Phenomena Interface',
        'Requirements Reference': 'requirements reference',
        'Requirements Constraint': 'requirements constraint'
    }

    # Tokenize the sentence
    tokens = re.findall(r'\b\w+\b', sentence)

    # Initialize the relation dictionary
    relation = {key: [] for key in schema_definition.values()}

    # Iterate over the entities
    for entity_type, entity_list in entities.items():
        # Iterate over the entities in the list
        for entity in entity_list:
            # Check if the entity is in the tokens
            if entity in tokens:
                # Iterate over the tokens
                for token in tokens:
                    # Check if the token is in the entity list
                    if token in entity_list:
                        # Add the relation to the dictionary
                        relation[schema_definition['Interface']].append([entity, token])
                    # Check if the token is in the system requirements
                    elif token in entities['System Requirements']:
                        # Add the relation to the dictionary
                        relation[schema_definition['Requirements Reference']].append([entity, token])
                    # Check if the token is in the shared phenomena
                    elif token in entities['Shared Phenomena']:
                        # Add the relation to the dictionary
                        relation[schema_definition['Requirements Constraint']].append([entity, token])

    # Return the relation dictionary as a JSON string
    return json.dumps(relation)

