import json

def extract_relation(sentence, entities):
    # Define the schema definition
    schema_definition = {
        'Interface': 'Phenomena Interface',
        'Requirements Reference': 'requirements reference',
        'Requirements Constraint': 'requirements constraint'
    }

    # Initialize the result dictionary
    result = {key: [] for key in schema_definition.values()}

    # Extract entities
    software_systems = entities['Software System']
    physical_devices = entities['Physical Device']
    environment_objects = entities['Environment Object']
    external_systems = entities['External System']
    system_requirements = entities['System Requirements']
    shared_phenomena = entities['Shared Phenomena']

    # Extract relations
    for requirement in system_requirements:
        for phenomenon in shared_phenomena:
            if phenomenon in requirement:
                result['requirements constraint'].append([requirement, phenomenon])
        for system in software_systems:
            if system in requirement:
                result['requirements reference'].append([system, requirement])
        for device in physical_devices:
            if device in requirement:
                result['requirements reference'].append([device, requirement])
        for object in environment_objects:
            if object in requirement:
                result['requirements reference'].append([object, requirement])
        for system in external_systems:
            if system in requirement:
                result['requirements reference'].append([system, requirement])

    for phenomenon in shared_phenomena:
        for system in software_systems:
            if phenomenon in sentence and phenomenon in system:
                result['Phenomena Interface'].append([system, phenomenon])
        for device in physical_devices:
            if phenomenon in sentence and phenomenon in device:
                result['Phenomena Interface'].append([device, phenomenon])
        for object in environment_objects:
            if phenomenon in sentence and phenomenon in object:
                result['Phenomena Interface'].append([object, phenomenon])
        for system in external_systems:
            if phenomenon in sentence and phenomenon in system:
                result['Phenomena Interface'].append([system, phenomenon])

    for system in software_systems:
        for device in physical_devices:
            if system in sentence and device in sentence:
                result['Phenomena Interface'].append([system, device])

    # Return the result as a JSON string
    return json.dumps(result)

