import json

def extract_relation(sentence, entities):
    # Define the schema definition
    schema_definition = ['Interface', 'Requirements Reference', 'Requirements Constraint']

    # Define the relation types
    relation_types = {
        'Interface': 'Phenomena Interface',
        'Requirements Reference': 'requirements reference',
        'Requirements Constraint': 'requirements constraint'
    }

    # Initialize the result dictionary
    result = {relation_type: [] for relation_type in schema_definition}

    # Tokenize the sentence
    tokens = sentence.split()

    # Extract the entities
    entity_list = entities['Software System'] + entities['Physical Device'] + entities['Environment Object'] + entities['External System'] + entities['System Requirements'] + entities['Shared Phenomena']

    # Iterate over the tokens
    for i in range(len(tokens)):
        # Check if the token is an entity
        if tokens[i] in entity_list:
            # Get the entity type
            entity_type = None
            if tokens[i] in entities['Software System']:
                entity_type = 'Software System'
            elif tokens[i] in entities['Physical Device']:
                entity_type = 'Physical Device'
            elif tokens[i] in entities['Environment Object']:
                entity_type = 'Environment Object'
            elif tokens[i] in entities['External System']:
                entity_type = 'External System'
            elif tokens[i] in entities['System Requirements']:
                entity_type = 'System Requirements'
            elif tokens[i] in entities['Shared Phenomena']:
                entity_type = 'Shared Phenomena'

            # Check if the entity is related to other entities
            for j in range(i+1, len(tokens)):
                if tokens[j] in entity_list:
                    # Get the related entity type
                    related_entity_type = None
                    if tokens[j] in entities['Software System']:
                        related_entity_type = 'Software System'
                    elif tokens[j] in entities['Physical Device']:
                        related_entity_type = 'Physical Device'
                    elif tokens[j] in entities['Environment Object']:
                        related_entity_type = 'Environment Object'
                    elif tokens[j] in entities['External System']:
                        related_entity_type = 'External System'
                    elif tokens[j] in entities['System Requirements']:
                        related_entity_type = 'System Requirements'
                    elif tokens[j] in entities['Shared Phenomena']:
                        related_entity_type = 'Shared Phenomena'

                    # Check if the relation type is 'Interface'
                    if entity_type == related_entity_type:
                        # Add the relation to the result dictionary
                        result['Phenomena Interface'].append([tokens[i], tokens[j]])
                    # Check if the relation type is 'Requirements Reference'
                    elif entity_type == 'System Requirements' and related_entity_type in ['Software System', 'Physical Device', 'Environment Object', 'External System']:
                        # Add the relation to the result dictionary
                        result['requirements reference'].append([tokens[i], tokens[j]])
                    # Check if the relation type is 'Requirements Constraint'
                    elif entity_type == 'System Requirements' and related_entity_type in ['Shared Phenomena']:
                        # Add the relation to the result dictionary
                        result['requirements constraint'].append([tokens[i], tokens[j]])

    # Return the result dictionary as a JSON string
    return json.dumps(result)

