import json

def extract_relation(sentence, entities):
    # Define the schema definition
    schema_definition = {
        'Interface': 'Phenomena Interface',
        'Requirements Reference': 'requirements reference',
        'Requirements Constraint': 'requirements constraint'
    }

    # Initialize the result dictionary
    result = {key: [] for key in schema_definition.values()}

    # Tokenize the sentence
    tokens = sentence.split()

    # Extract entities
    entity_dict = {}
    for entity_type, entity_list in entities.items():
        for entity in entity_list:
            if entity not in entity_dict:
                entity_dict[entity] = {'type': entity_type, 'tokens': []}
            for token in tokens:
                if token == entity:
                    entity_dict[entity]['tokens'].append(token)

    # Extract relations
    for entity, info in entity_dict.items():
        if info['type'] == 'System Requirements':
            for token in tokens:
                if token not in info['tokens']:
                    for other_entity, other_info in entity_dict.items():
                        if other_entity != entity and other_info['type'] != 'System Requirements':
                            if token in other_info['tokens']:
                                result[schema_definition['Requirements Reference']].append([entity, token])
        elif info['type'] == 'Shared Phenomena':
            for token in tokens:
                if token not in info['tokens']:
                    for other_entity, other_info in entity_dict.items():
                        if other_entity != entity and other_info['type'] != 'Shared Phenomena':
                            if token in other_info['tokens']:
                                result[schema_definition['Interface']].append([entity, token])
        elif info['type'] == 'Software System':
            for token in tokens:
                if token not in info['tokens']:
                    for other_entity, other_info in entity_dict.items():
                        if other_entity != entity and other_info['type'] != 'Software System':
                            if token in other_info['tokens']:
                                if other_info['type'] == 'Shared Phenomena':
                                    result[schema_definition['Requirements Constraint']].append([entity, token])
                                elif other_info['type'] == 'System Requirements':
                                    result[schema_definition['Requirements Reference']].append([entity, token])

    # Convert the result to a JSON string
    return json.dumps(result)

