        if relation_type == 'Interface':
            for entity in entity_list:
                if entity in sentence:
                    # Extract the phenomenon
                    phenomenon = re.search(r'\b' + entity + r'\b', sentence).group()
                    # Extract the interface
                    interface = re.search(r'\b' + entity + r'\b', sentence).group()
                    # Add the relation to the result
                    result[relation_types[relation_type]].append([interface, phenomenon])
        elif relation_type == 'Requirements Reference':
            for entity in entity_list:
                if entity in sentence:
                    # Extract the requirement
                    requirement = re.search(r'\b' + entity + r'\b', sentence).group()
                    # Extract the reference
                    reference = re.search(r'\b' + entity + r'\b', sentence).group()
                    # Add the relation to the result
                    result[relation_types[relation_type]].append([reference, requirement])
        elif relation_type == 'Requirements Constraint':
            for entity in entity_list:
                if entity in sentence:
                    # Extract the requirement
                    requirement = re.search(r'\b' + entity + r'\b', sentence).group()
                    # Extract the constraint
                    constraint = re.search(r'\b' + entity + r'\b', sentence).group()
                    # Add the relation to the result
                    result[relation_types[relation_type]].append([constraint, requirement])

    # Return the result as a JSON string
    return json.dumps(result)

