                        # Use regular expression to find the interface relation
                        interface_relation = re.search(r'\b' + re.escape(entity) + r'\s+is\s+an\s+interface\s+of\s+sending/receving\s+data\s+between\s+the\s+connected\s+entities\s+and\s+\b' + re.escape(entity) + r'\b', sentence)
                        if interface_relation:
