                        # Use regular expression to find the requirements constraint relation
                        requirements_constraint_relation = re.search(r'\b' + re.escape(entity) + r'\s+shall\s+allow\s+\b' + re.escape(sentence) + r'\b\s+if\s+required\s+and\s+provided\s+by\s+\b' + re.escape(entity) + r'\b', sentence)
                        if requirements_constraint_relation:
                            # Add the requirements constraint relation to the result
                            result['requirements constraint'].append([entity, sentence])

    # Return the result as a JSON string
    return json.dumps(result)

