    # Extract entities
    entity_dict = {}
    for entity_type, entity_list in entities.items():
        for entity in entity_list:
            entity_dict[entity] = entity_type

    # Extract relations
    for token in tokens:
        if token in entity_dict:
            entity_type = entity_dict[token]
            if entity_type == 'Software System' or entity_type == 'External System':
                if token in result['Interface']:
                    result['Interface'].remove(token)
                result['Interface'].append(token)
            elif entity_type == 'System Requirements':
                if token in result['Requirements Reference']:
                    result['Requirements Reference'].remove(token)
                result['Requirements Reference'].append(token)
            elif entity_type == 'Shared Phenomena':
                if token in result['Requirements Constraint']:
                    result['Requirements Constraint'].remove(token)
                result['Requirements Constraint'].append(token)
            else:
                if token in result['Requirements Reference']:
                    result['Requirements Reference'].remove(token)
                result['Requirements Reference'].append(token)

    # Remove duplicates
    for key in result:
        result[key] = list(set(result[key]))

    # Return the result as a JSON string
    return json.dumps(result)

