                        result[relation_types['Requirements Reference']].append([token, other_entity])
            elif entity_type == 'Shared Phenomena':
                for other_entity, other_entity_type in entities.items():
                    if other_entity != token and other_entity_type != 'Shared Phenomena':
                        result[relation_types['Interface']].append([token, other_entity])
            elif entity_type == 'Software System' or entity_type == 'Physical Device':
