                referencing_entities = [e for e, r in entities.items() if relation in r]
                # Add the relation to the result dictionary
                for referencing_entity in referencing_entities:
                    result[schema_definition['Requirements Reference']].append([referencing_entity, relation])
            # Check if the relation is a shared phenomenon that constrains a system requirement
            elif relation in entities['Shared Phenomena'] and relation in entities['System Requirements']:
                # Extract the entities that reference the system requirement
                referencing_entities = [e for e, r in entities.items() if relation in r]
