                        elif entity_name in entities['System Requirements'] and other_entity_name in entities['Shared Phenomena']:
                            result[schema_definition['Requirements Constraint']].append([entity_name, other_entity_name])

    # Return the result as a JSON string
    return json.dumps(result)

