                            result[schema_definition['Requirements Reference']].append([requirement, entity_value])
        elif entity == 'Shared Phenomena':
            for phenomenon in value:
                for entity_name, entity_value in entity_list:
                    if entity_name != 'Shared Phenomena':
                        if re.search(r'\b' + phenomenon + r'\b', entity_value):
                            if schema_definition['Interface'] not in result:
                                result[schema_definition['Interface']] = []
                            result[schema_definition['Interface']].append([entity_value, phenomenon])
        elif entity == 'Software System' or entity == 'External System':
            for entity_name, entity_value in entity_list:
                if entity_name != 'Software System' and entity_name != 'External System':
                    if re.search(r'\b' + entity_value + r'\b', value):
                        if schema_definition['Interface'] not in result:
                            result[schema_definition['Interface']] = []
                        result[schema_definition['Interface']].append([entity_value, entity_name])

    # Return the result as a JSON string
    return json.dumps(result)

