                for entity in entity_list:
                    # Use regular expression to extract the requirements reference relation
                    pattern = r'\b' + re.escape(entity) + r'\b'
                    matches = re.findall(pattern, sentence)
                    for match in matches:
                        if match not in result[relation_type]:
                            result[relation_type].append([entity, match])
        elif relation_type == 'Requirements Constraint':
            # Extract the requirements constraint relation
