    for entity in entities:
        for key, value in entity.items():
            if key in entity_list:
                for relation in schema_definition:
                    if relation == 'Interface':
                        # Extract the interface relation
                        for entity_value in value:
                            if entity_value in entity_list:
                                result[relation].append([key, entity_value])
                    elif relation == 'Requirements Reference':
                        # Extract the requirements reference relation
                        for entity_value in value:
                            if entity_value in entity_list:
                                for entity_key, entity_value_list in entities.items():
                                    if entity_key != key and entity_value in entity_value_list:
                                        result[relation].append([entity_value, entity_value])
                    elif relation == 'Requirements Constraint':
                        # Extract the requirements constraint relation
                        for entity_value in value:
                            if entity_value in entity_list:
                                for entity_key, entity_value_list in entities.items():
                                    if entity_key != key and entity_value in entity_value_list:
                                        result[relation].append([entity_value, entity_value])

    # Return the result
    return result

