import re
from collections import defaultdict

def extract_relation(sentence, entities):
    # Define the schema definition
    schema_definition = {
        'Interface': 'Phenomena Interface',
        'Requirements Reference': 'requirements reference',
        'Requirements Constraint': 'requirements constraint'
    }

    # Initialize the result dictionary
    result = {key: [] for key in schema_definition.values()}

    # Tokenize the sentence
    tokens = re.findall(r'\b\w+\b', sentence)

    # Extract entities
    entity_dict = defaultdict(list)
    for entity in entities:
        for token in tokens:
            if token in entities[entity]:
                entity_dict[entity].append(token)

    # Extract relations
    for entity, entity_list in entity_dict.items():
        if entity == 'System Requirements':
            for token in tokens:
                if token in entities['System Requirements']:
                    for other_entity, other_entity_list in entity_dict.items():
                        if other_entity != 'System Requirements' and token in other_entity_list:
                            result[schema_definition['Requirements Reference']].append([token, other_entity])
                            result[schema_definition['Requirements Constraint']].append([token, other_entity])
        elif entity == 'Shared Phenomena':
            for token in tokens:
                if token in entities['Shared Phenomena']:
                    for other_entity, other_entity_list in entity_dict.items():
                        if other_entity != 'Shared Phenomena' and token in other_entity_list:
                            result[schema_definition['Interface']].append([token, other_entity])
                            result[schema_definition['Requirements Reference']].append([token, other_entity])
                            result[schema_definition['Requirements Constraint']].append([token, other_entity])
        else:
            for token in tokens:
                if token in entities[entity]:
                    for other_entity, other_entity_list in entity_dict.items():
                        if other_entity != entity and token in other_entity_list:
                            result[schema_definition['Interface']].append([token, other_entity])
                            result[schema_definition['Requirements Reference']].append([token, other_entity])
                            result[schema_definition['Requirements Constraint']].append([token, other_entity])

    return {key: value for key, value in result.items() if value}

