import json

def extract_relation(sentence, entities):
    # Define the schema definition
    schema_definition = ['Interface', 'Requirements Reference', 'Requirements Constraint']

    # Define the relation types
    relation_types = {
        'Interface': lambda x, y: (x in entities['Physical Device'] or x in entities['Software System']) and (y in entities['Physical Device'] or y in entities['Software System']),
        'Requirements Reference': lambda x, y: (x in entities['System Requirements'] or x in entities['Shared Phenomena']) and (y in entities['Physical Device'] or y in entities['Software System'] or y in entities['External System']),
        'Requirements Constraint': lambda x, y: (x in entities['System Requirements'] or x in entities['Shared Phenomena']) and (y in entities['Physical Device'] or y in entities['Software System'] or y in entities['External System'])
    }

    # Initialize the result
    result = {relation_type: [] for relation_type in schema_definition}

    # Extract the entities
    entity_list = []
    for key, value in entities.items():
        entity_list.extend(value)

    # Extract the relations
    for i in range(len(entity_list)):
        for j in range(i+1, len(entity_list)):
            for relation_type in schema_definition:
                if relation_types[relation_type](entity_list[i], entity_list[j]):
                    result[relation_type].append([entity_list[i], entity_list[j]])

    # Return the result as a JSON string
    return json.dumps(result)

