    schema_definition = {
        'Interface': lambda x, y: re.search(r'\b' + x + r'\b', y),
        'Requirements Reference': lambda x, y: re.search(r'\b' + x + r'\b', y) and re.search(r'\b' + y + r'\b', x),
        'Requirements Constraint': lambda x, y: re.search(r'\b' + x + r'\b', y) and re.search(r'\b' + y + r'\b', x)
    }

    # Initialize the result dictionary
    result = defaultdict(list)

    # Loop through each entity type
    for entity_type, entity_list in entities.items():
        # Loop through each entity
        for entity in entity_list:
            # Loop through each schema definition
            for schema_type, schema_func in schema_definition.items():
                # Check if the entity matches the schema definition
                if schema_func(entity, sentence):
                    # Add the relation to the result dictionary
                    result[schema_type + ' Interface'].append([entity, sentence])

    # Convert the result dictionary to a JSON string
    return str(result)

