    schema_definition = ['Interface', 'Requirements Reference', 'Requirements Constraint']

    # Initialize the result dictionary
    result = {relation: [] for relation in schema_definition}

    # Extract entities
    entity_list = []
    for entity in entities:
        if entities[entity]:
            entity_list.extend(entities[entity])

    # Extract relation
    for relation in schema_definition:
        if relation == 'Interface':
            pattern = r'between|send|receive|interface|connect|communicate|data|information|resource|system|hardware|software'
            result[relation] = re.findall(pattern, sentence, re.IGNORECASE)
        elif relation == 'Requirements Reference':
            pattern = r'reference|refer|requirement|require|specify|specification|document|section|subsection|number|list|table|format'
            result[relation] = re.findall(pattern, sentence, re.IGNORECASE)
        elif relation == 'Requirements Constraint':
            pattern = r'constraint|constrain|limit|bound|condition|condition|requirement|require|specify|specification|document|section|subsection|number|list|table|format'
            result[relation] = re.findall(pattern, sentence, re.IGNORECASE)

    return result

