            for other_entity in entity_list:
                if other_entity != entity:
                    if other_entity in entities['External System'] or other_entity in entities['Software System'] or other_entity in entities['Physical Device'] or other_entity in entities['Environment Object']:
                        result[schema_definition['Interface']].append([entity, other_entity])
        elif entity in entities['External System'] or entity in entities['Software System'] or entity in entities['Physical Device'] or entity in entities['Environment Object']:
            for other_entity in entity_list:
                if other_entity != entity:
                    if other_entity in entities['Shared Phenomena']:
                        result[schema_definition['Interface']].append([entity, other_entity])

    # Return the result as a JSON string
    return json.dumps(result)

