import json

def extract_relation(sentence, entities):
    # Define the schema definition
    schema_definition = {
        'Interface': 'Phenomena Interface',
        'Requirements Reference': 'requirements reference',
        'Requirements Constraint': 'requirements constraint'
    }

    # Initialize the result dictionary
    result = {key: [] for key in schema_definition.values()}

    # Tokenize the sentence
    tokens = sentence.split()

    # Extract entities
    entity_dict = {}
    for entity_type, entity_list in entities.items():
        for entity in entity_list:
            if entity not in entity_dict:
                entity_dict[entity] = entity_type

    # Extract relations
    for i in range(len(tokens)):
        for j in range(i + 1, len(tokens)):
            # Check if the tokens are entities
            if tokens[i] in entity_dict and tokens[j] in entity_dict:
                # Check if the relation type is 'Interface'
                if entity_dict[tokens[i]] == 'Software System' and entity_dict[tokens[j]] == 'Physical Device':
                    result[schema_definition['Interface']].append([tokens[i], tokens[j]])
                elif entity_dict[tokens[i]] == 'Physical Device' and entity_dict[tokens[j]] == 'Software System':
                    result[schema_definition['Interface']].append([tokens[j], tokens[i]])
                # Check if the relation type is 'Requirements Reference'
                elif entity_dict[tokens[i]] == 'Software System' and entity_dict[tokens[j]] == 'Software System':
                    result[schema_definition['Requirements Reference']].append([tokens[i], tokens[j]])
                # Check if the relation type is 'Requirements Constraint'
                elif entity_dict[tokens[i]] == 'Software System' and entity_dict[tokens[j]] == 'System Requirements':
                    result[schema_definition['Requirements Constraint']].append([tokens[i], tokens[j]])

    # Return the result as a JSON string
    return json.dumps(result)

