            "name": physical_device.group()
        })
    # Environment Object
    environment_object = re.search(r'\b(patient|sun|old|environment|object|entity)\b', sentence)
    if environment_object:
        entities.append({
            "type": "Environment Object",
            "name": environment_object.group()
        })
    # External System
    external_system = re.search(r'\b(database|magnetic stripe card|tap|hard disk|system|network)\b', sentence)
    if external_system:
        entities.append({
            "type": "External System",
            "name": external_system.group()
        })
    # System Requirements
    system_requirements = re.search(r'\b(requirement|aim|purpose|goal|objective|requirement|need|wish|expectation)\b', sentence)
    if system_requirements:
        entities.append({
            "type": "System Requirements",
