    shared_phenomena = re.search(r'\b(network|identifier|Ramp Meter|username|password|plan|event|state|value|phenomena)\b', sentence)
    if shared_phenomena:
        entities.append({
            "type": "Shared Phenomena",
            "name": shared_phenomena.group()
