def extract_entities(sentence):
    entities = []
    # Software System
    software_systems = re.findall(r'(\w+)\s*shall\s*be\s*able\s*to\s*(\w+)', sentence)
    for software_system, requirement in software_systems:
        entities.append({
            "type": "Software System",
            "name": software_system
        })
    # Physical Device
    physical_devices = re.findall(r'(\w+)\s*control\s*command\s*for\s*(\w+)', sentence)
    for device, device_type in physical_devices:
        entities.append({
            "type": "Physical Device",
            "name": device
        })
    # Environment Object
    environment_objects = re.findall(r'(\w+)\s*(?:the|a)?\s*(\w+)', sentence)
    for object, object_type in environment_objects:
        entities.append({
            "type": "Environment Object",
            "name": object
        })
    # External System
    external_systems = re.findall(r'(\w+)\s*(?:identifier|ID|ID\s*number)', sentence)
    for system, system_type in external_systems:
        entities.append({
            "type": "External System",
            "name": system
        })
    # System Requirements
    system_requirements = re.findall(r'(\w+)\s*control', sentence)
    for requirement in system_requirements:
        entities.append({
            "type": "System Requirements",
            "name": requirement
        })
    # Shared Phenomena
    shared_phenomena = re.findall(r'(\w+)\s*control\s*command', sentence)
    for phenomenon in shared_phenomena:
        entities.append({
            "type": "Shared Phenomena",
            "name": phenomenon
        })
    return entities

