import re
import json

def extract_entities(sentence):
    entities = []
    # Software System
    software_systems = re.findall(r'(\w+)\s*shall\s*be\s*able\s*to\s*(\w+)', sentence)
    for software_system, system_requirements in software_systems:
        entities.append({
            "type": "Software System",
            "name": software_system
        })
        entities.append({
            "type": "System Requirements",
            "name": system_requirements
        })
    # Physical Device
    physical_devices = re.findall(r'(\w+)\s*control\s*command\s*for\s*(\w+)', sentence)
    for device, physical_device in physical_devices:
        entities.append({
            "type": "Physical Device",
            "name": physical_device
        })
        entities.append({
            "type": "System Requirements",
            "name": device
        })
    # Environment Object
    environment_objects = re.findall(r'(\w+)', sentence)
    for obj in environment_objects:
        if obj not in [device for device in physical_devices]:
            entities.append({
                "type": "Environment Object",
                "name": obj
            })
    # External System
    external_systems = re.findall(r'(\w+)\s*identifier', sentence)
    for system in external_systems:
        entities.append({
            "type": "External System",
            "name": system
        })
    # Shared Phenomena
    shared_phenomena = re.findall(r'(\w+)\s*identifier', sentence)
    for phenomenon in shared_phenomena:
        entities.append({
            "type": "Shared Phenomena",
            "name": phenomenon
        })
    return json.dumps(entities)

