## Code
```python
import re

def extract_entities(sentence):
    entities = []
    schema = ['Software System', 'Physical Device', 'Environment Object', 'External System', 'System Requirements', 'Shared Phenomena']

    # Software System
    software_system = re.findall(r'([A-Za-z0-9\s]+) shall', sentence)
    for system in software_system:
        entities.append({
            "type": "Software System",
            "name": system.strip()
        })

    # Physical Device
    physical_device = re.findall(r'([A-Za-z0-9\s]+) device', sentence)
    for device in physical_device:
        entities.append({
            "type": "Physical Device",
            "name": device.strip()
        })

    # Environment Object
    environment_object = re.findall(r'([A-Za-z0-9\s]+)', sentence)
    for obj in environment_object:
        if obj not in [system.strip() for system in software_system] and obj not in [device.strip() for device in physical_device]:
            entities.append({
                "type": "Environment Object",
                "name": obj.strip()
            })

    # External System
    external_system = re.findall(r'([A-Za-z0-9\s]+) identifier', sentence)
    for system in external_system:
        entities.append({
            "type": "External System",
            "name": system.strip()
        })

    # System Requirements
    system_requirements = re.findall(r'([A-Za-z0-9\s]+) request', sentence)
    for requirement in system_requirements:
        entities.append({
            "type": "System Requirements",
            "name": requirement.strip()
        })

    # Shared Phenomena
    shared_phenomena = re.findall(r'([A-Za-z0-9\s]+) accepted', sentence)
    for phenomenon in shared_phenomena:
        entities.append({
            "type": "Shared Phenomena",
            "name": phenomenon.strip()
        })

    return entities

