    if software_system:
        entities.append({
            "type": "Software System",
            "name": software_system.group()
        })
    # Physical Device
    physical_device = re.search(r'(\w+\s\w+|\w+)', sentence)
    if physical_device:
        entities.append({
            "type": "Physical Device",
            "name": physical_device.group()
        })
    # Environment Object
    environment_object = re.search(r'(\w+\s\w+|\w+)', sentence)
    if environment_object:
        entities.append({
            "type": "Environment Object",
            "name": environment_object.group()
        })
    # External System
    external_system = re.search(r'(\w+\s\w+|\w+)', sentence)
    if external_system:
        entities.append({
            "type": "External System",
            "name": external_system.group()
        })
    # System Requirements
    system_requirements = re.search(r'(\w+\s\w+|\w+)', sentence)
    if system_requirements:
        entities.append({
            "type": "System Requirements",
            "name": system_requirements.group()
        })
    # Shared Phenomena
    shared_phenomena = re.search(r'(\w+\s\w+|\w+)', sentence)
    if shared_phenomena:
        entities.append({
            "type": "Shared Phenomena",
            "name": shared_phenomena.group()
        })
    return entities

