def extract_entities(sentence):
    entities = []
    # Software System
    software_systems = re.findall(r'\bthe correlator\b', sentence, re.IGNORECASE)
    for system in software_systems:
        entities.append({
            "type": "Software System",
            "name": system
        })
    # Physical Device
    physical_devices = re.findall(r'\bhardware\b', sentence, re.IGNORECASE)
    for device in physical_devices:
        entities.append({
            "type": "Physical Device",
            "name": device
        })
    # Environment Object
    environment_objects = re.findall(r'\bthe A second interface\b', sentence, re.IGNORECASE)
    for obj in environment_objects:
        entities.append({
            "type": "Environment Object",
            "name": obj
        })
    # External System
    external_systems = re.findall(r'\bthe same table structures\b', sentence, re.IGNORECASE)
    for system in external_systems:
        entities.append({
            "type": "External System",
            "name": system
        })
    return entities

