import re

def extract_entities(sentence):
    entities = []
    # Software System
    software_systems = re.findall(r'ASPERA-\d', sentence)
    for system in software_systems:
        entities.append({
            "type": "Software System",
            "name": system
        })
    # Physical Device
    physical_devices = re.findall(r'MEX OA', sentence)
    for device in physical_devices:
        entities.append({
            "type": "Physical Device",
            "name": device
        })
    # Environment Object
    environment_objects = re.findall(r'local SwRI archive', sentence)
    for object in environment_objects:
        entities.append({
            "type": "Environment Object",
            "name": object
        })
    # External System
    external_systems = re.findall(r'local SwRI archive', sentence)
    for system in external_systems:
        entities.append({
            "type": "External System",
            "name": system
        })
    # System Requirements
    system_requirements = re.findall(r'data availability and re-processing', sentence)
    for requirement in system_requirements:
        entities.append({
            "type": "System Requirements",
            "name": requirement
        })
    # Shared Phenomena
    shared_phenomena = re.findall(r'data availability and re-processing', sentence)
    for phenomenon in shared_phenomena:
        entities.append({
            "type": "Shared Phenomena",
            "name": phenomenon
        })
    return entities

