import re

def extract_entities(sentence):
    entities = {"Software System": [], "Physical Device": [], "Environment Object": [], "External System": [], "System Requirements": [], "Shared Phenomena": []}

    # Software System
    software_systems = re.findall(r'\b(system|software|application|program|system|software|application|program)\b', sentence, re.IGNORECASE)
