import re
import json

def extract_entities(sentence):
    entities = []
    # Software System
    software_system = re.search(r'(\w+) the (\w+)', sentence)
    if software_system:
        entities.append({
            "type": "Software System",
            "name": software_system.group(2)
        })
    # Physical Device
    physical_device = re.search(r'(\w+) a (\w+)', sentence)
    if physical_device:
        entities.append({
            "type": "Physical Device",
            "name": physical_device.group(2)
        })
    # Environment Object
    environment_object = re.search(r'(\w+) (\w+)', sentence)
    if environment_object:
        entities.append({
            "type": "Environment Object",
            "name": environment_object.group(2)
        })
    # External System
    external_system = re.search(r'(\w+) (\w+)', sentence)
    if external_system:
        entities.append({
            "type": "External System",
            "name": external_system.group(2)
        })
    # System Requirements
    system_requirements = re.search(r'the aim and purpose of the system to be developed is (\w+)', sentence)
    if system_requirements:
        entities.append({
            "type": "System Requirements",
            "name": system_requirements.group(1)
        })
    # Shared Phenomena
    shared_phenomena = re.search(r'a set of shared events, states and values between the connected entities is (\w+)', sentence)
    if shared_phenomena:
        entities.append({
            "type": "Shared Phenomena",
            "name": shared_phenomena.group(1)
        })
    return json.dumps(entities)

