import re

def extract_entities(sentence):
    entities = []
    schema = {
        'Software System': r'\b(Tactical Control System|AV|data link)\b',
        'Physical Device': r'\b(physical device|AV|data link)\b',
        'Environment Object': r'\b(flight route plan|flight route|route plan|route)\b',
        'External System': r'\b(database|magnetic stripe card|tap|hard disk)\b',
        'System Requirements': r'\b(flight constraints|limitation of the data link|data link)\b',
        'Shared Phenomena': r'\b(transmission of the flight route plan|transmission|flight route plan)\b'
    }

    for entity_type, pattern in schema.items():
        matches = re.findall(pattern, sentence)
        for match in matches:
            entities.append({
                'type': entity_type,
                'name': match
            })

    return entities

sentence = "The Tactical Control System shall analyze the flight route plan selected for uplink to determine that the flight constraints of the AV and the limitation of the data link are not violated prior to transmission of the flight route plan to the AV."
print(extract_entities(sentence))
