import json

def extract_entities(sentence):
    entities = {
        "Software System": [],
        "Physical Device": [],
        "Environment Object": [],
        "External System": [],
        "System Requirements": [],
        "Shared Phenomena": []
    }

    # Tokenize the sentence
    tokens = sentence.split()

    # Identify Software System
    for token in tokens:
        if token.lower() == "system":
            entities["Software System"].append(token)

    # Identify Physical Device
    for token in tokens:
        if token.lower() == "pedestal" or token.lower() == "antenna" or token.lower() == "platform":
            entities["Physical Device"].append(token)

    # Identify Environment Object
    for token in tokens:
        if token.lower() == "ship" or token.lower() == "hmmwv" or token.lower() == "motion":
            entities["Environment Object"].append(token)

    # Identify External System
    for token in tokens:
        if token.lower() == "database" or token.lower() == "card" or token.lower() == "disk":
            entities["External System"].append(token)

    # Identify System Requirements
    for token in tokens:
        if token.lower() == "shall" or token.lower() == "incorporate" or token.lower() == "compensate":
            entities["System Requirements"].append(token)

    # Identify Shared Phenomena
    for token in tokens:
        if token.lower() == "motion":
            entities["Shared Phenomena"].append(token)

    return json.dumps(entities)

