import re
import json

def extract_entities(sentence):
    entities = {
        'Software System': [],
        'Physical Device': [],
        'Environment Object': [],
        'External System': [],
        'System Requirements': [],
        'Shared Phenomena': []
    }

    # Software System
    software_system_pattern = r"(\w+\s*\w*)\s*shall\s*(\w+)"
    software_system_matches = re.findall(software_system_pattern, sentence)
    for match in software_system_matches:
        entities['Software System'].append(match[0])

    # Physical Device
    physical_device_pattern = r"(\w+\s*\w*)\s*(shall|will)\s*use|(\w+\s*\w*)\s*(shall|will)\s*communicate|(\w+\s*\w*)\s*(shall|will)\s*send|(\w+\s*\w*)\s*(shall|will)\s*receive"
    physical_device_matches = re.findall(physical_device_pattern, sentence)
    for match in physical_device_matches:
        if match[0]:
            entities['Physical Device'].append(match[0])
        elif match[2]:
            entities['Physical Device'].append(match[2])
        elif match[4]:
            entities['Physical Device'].append(match[4])
        elif match[6]:
            entities['Physical Device'].append(match[6])

    # Environment Object
    environment_object_pattern = r"(\w+\s*\w*)\s*(shall|will)\s*be|(\w+\s*\w*)\s*(shall|will)\s*have|(\w+\s*\w*)\s*(shall|will)\s*contain|(\w+\s*\w*)\s*(shall|will)\s*support"
    environment_object_matches = re.findall(environment_object_pattern, sentence)
    for match in environment_object_matches:
        if match[0]:
            entities['Environment Object'].append(match[0])
        elif match[2]:
            entities['Environment Object'].append(match[2])
        elif match[4]:
            entities['Environment Object'].append(match[4])
        elif match[6]:
            entities['Environment Object'].append(match[6])

    # External System
    external_system_pattern = r"(\w+\s*\w*)\s*(shall|will)\s*use|(\w+\s*\w*)\s*(shall|will)\s*communicate|(\w+\s*\w*)\s*(shall|will)\s*send|(\w+\s*\w*)\s*(shall|will)\s*receive|(\w+\s*\w*)\s*(shall|will)\s*transmit|(\w+\s*\w*)\s*(shall|will)\s*exchange"
    external_system_matches = re.findall(external_system_pattern, sentence)
    for match in external_system_matches:
        if match[0]:
            entities['External System'].append(match[0])
        elif match[2]:
            entities['External System'].append(match[2])
        elif match[4]:
            entities['External System'].append(match[4])
        elif match[6]:
            entities['External System'].append(match[6])
        elif match[8]:
            entities['External System'].append(match[8])
        elif match[10]:
            entities['External System'].append(match[10])

    # System Requirements
    system_requirements_pattern = r"(\w+\s*\w*)\s*(shall|will)\s*(\w+)"
    system_requirements_matches = re.findall(system_requirements_pattern, sentence)
    for match in system_requirements_matches:
        entities['System Requirements'].append(match[2])

    # Shared Phenomena
    shared_phenomena_pattern = r"(\w+\s*\w*)\s*(shall|will)\s*(\w+)"
    shared_phenomena_matches = re.findall(shared_phenomena_pattern, sentence)
    for match in shared_phenomena_matches:
        entities['Shared Phenomena'].append(match[2])

    return json.dumps(entities)

# Test the function
print(extract_entities("The Tactical Control System will have the functionality to process received C4I messages and to prepare C4I messages for transmission."))
