import re

def extract_entities(sentence):
    entities = {
        'Software System': [],
        'Physical Device': [],
        'Environment Object': [],
        'External System': [],
        'System Requirements': [],
        'Shared Phenomena': []
    }

    # Software System
    software_systems = re.findall(r'\b\w+\s\w+\b', sentence)
    for system in software_systems:
        entities['Software System'].append(system)

    # Physical Device
    physical_devices = re.findall(r'\b\w+\s\w+\b', sentence)
    for device in physical_devices:
        if device not in entities['Software System']:
            entities['Physical Device'].append(device)

    # Environment Object
    environment_objects = re.findall(r'\b\w+\b', sentence)
    for obj in environment_objects:
        if obj not in entities['Software System'] and obj not in entities['Physical Device']:
            entities['Environment Object'].append(obj)

    # External System
    external_systems = re.findall(r'\b\w+\s\w+\b', sentence)
    for system in external_systems:
        if system not in entities['Software System'] and system not in entities['Physical Device']:
            entities['External System'].append(system)

    # System Requirements
    system_requirements = re.findall(r'\b\w+\s\w+\b', sentence)
    for requirement in system_requirements:
        if requirement not in entities['Software System'] and requirement not in entities['Physical Device'] and requirement not in entities['External System']:
            entities['System Requirements'].append(requirement)

    # Shared Phenomena
    shared_phenomena = re.findall(r'\b\w+\s\w+\b', sentence)
    for phenomenon in shared_phenomena:
        if phenomenon not in entities['Software System'] and phenomenon not in entities['Physical Device'] and phenomenon not in entities['External System'] and phenomenon not in entities['System Requirements']:
            entities['Shared Phenomena'].append(phenomenon)

    return entities

